    constructor(props) {
        super(props);
        this.state = {flipped: false};
        this.flip = this.flip.bind(this);
    }

    flip = () => {
        this.setState({
            flipped: !this.state.flipped
        })
    }

    render(){
    return(
        <div onClick={this.flip} className={"card-container" + (this.state.flipped ? " flipped" : "")}>

